import React, { Component } from 'react'
import axios from 'axios';
import Summary from './Summary'
import Countries from './Countries'
import logo from './covid.jpg';
import {Navbar,Table} from 'react-bootstrap';

export default class Details extends Component {
    state={
        countries:[],
        global:[],
        currentDate:null,
        loading:true
    }
   async componentDidMount()
   {
       const res=await axios.get("https://api.covid19api.com/summary");
       console.log(res);
       this.setState({countries:res.data.Countries});
       this.setState({global:res.data.Global});
       this.setState({currentDate:res.data.Date});
       this.setState({loading:false});
   }
    render() {
        if(this.state.loading)
        {
            return<h1>Loading..........</h1>
        }
        return (
            <div>
            <Navbar>
  <Navbar.Brand href="#home"> <h1 style={{fontSize:'65px',fontFamily:' Brush Script Std,cursive'}}>C<span><img className="img" src={logo} alt="covid-img"/></span>VID-19 TRACKER</h1></Navbar.Brand>
  <Navbar.Toggle />

</Navbar>
        
            <Summary summary={this.state.global} currentDate={this.state.currentDate}/>
            <br/>
            <Table striped bordered hover variant="dark"  responsiv>

                <thead>
                    <tr>
                        <th>Country</th>
                        <th>New Confirmed</th>
                        <th>Total Confirmed</th>
                        <th>Total Recovered</th>
                        <th>Total Deaths</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.countries.map(country=>{
                    return <Countries countries={country} key={country.Country}/>
                })}
                </tbody>
            </Table>
                
            </div>
        )
    }
}
