import React, { Component } from 'react'
import {Form,Table} from 'react-bootstrap';

export default class Countries extends Component {
    state={
        inp:''
    }
    onchange=(e)=>{this.setState({inp:e.target.value})}


    render() {
        const {countries}=this.props;
        console.log(countries);
        return (
            <>
            <Form.Control type="text" placeholder="Search By Country Name........" onChange={this.onchange} />
            <Table striped bordered hover variant="dark"  responsive >

<thead>
    <tr>
        <th>Country</th>
        <th>New Confirmed</th>
        <th>Total Confirmed</th>
        <th>Total Recovered</th>
        <th>Total Deaths</th>
    </tr>
</thead>
<tbody>
{countries.filter((country)=>{
    if(this.state.inp==='')
    {
        return country
    }
})}
            <tr>
                <td>{countries.Country}</td>
                <td>{countries.NewConfirmed}</td>
                <td>{countries.TotalConfirmed}</td>
                <td>{countries.TotalRecovered}</td>
                <td>{countries.TotalDeaths}</td>
            </tr>
            </tbody>
            </Table>

            </>
                
          
        )
    }
}
