import React, { Component } from 'react'
import axios from 'axios';
import Summary from './Summary'
import Countries from './Countries'
import logo from './covid.png';
import {Navbar,Table} from 'react-bootstrap';

export default class Details extends Component {
    state={
        countries:[],
        global:[],
        currentDate:null,
        loading:true,
        searchTerm:""
    }
    onInputchange=(event) =>{
        this.setState({
          searchTerm: event.target.value
        });
        console.log(event.target.value);
      }
   
   async componentDidMount()
   {
       const res=await axios.get("https://api.covid19api.com/summary");
       console.log(res);
       this.setState({countries:res.data.Countries});
       this.setState({global:res.data.Global});
       this.setState({currentDate:res.data.Date});
       this.setState({loading:false});
   }
  

    render() {
        if(this.state.loading)
        {
            return<h1>Loading..........</h1>
        }
        return (
            <div>
            <Navbar >
  <Navbar.Brand > <h1 style={{fontSize:'65px',fontFamily:' Brush Script Std,cursive'}}>C<span><img className="img" src={logo} alt="covid-img"/></span>VID-19 TRACKER</h1></Navbar.Brand>
  <Navbar.Toggle />

</Navbar>
        
            <Summary summary={this.state.global} currentDate={this.state.currentDate}/>
            <br/>
            <input type="text" className="form-control" placeholder="Search Country By Name"  />
            <button type="button" className="btn btn primary" onClick={this.onInputchange}>Search</button>
            <div className="container">
            <h1>Data Of Different Countries</h1>
            <Table striped bordered hover variant="dark"  responsive >

                <thead>
                    <tr>
                        <th>Country</th>
                        <th>New Confirmed</th>
                        <th>Total Confirmed</th>
                        <th>Total Recovered</th>
                        <th>Total Deaths</th>
                    </tr>
                </thead>
                <tbody>
                {this.state.countries.filter((val)=>{
                    if(this.state.searchTerm===""){
                        console.log(val);
                        return val
                    }
                    else if(val.Country.toLowerCase().includes(this.state.searchTerm.toLowerCase()))
                    {
                        return  <Countries countries={val.Country} key={val.Country}/>

                    }

                    return null;
                  

                })
                .map(country=>{
                    return <Countries countries={country} key={country.Country}/>
                })}
                </tbody>
            </Table>
            </div>
                
            </div>
        )
    }
}
